const _ = require('lodash');
const CONTRACTS = require('../consts/CONTRACTS');
const fetchAllWaxData2 = require('../../core/fetchAllWaxData2');

const fetchPlayerInfo = async ({
    accountName,
}) => {
    const playerInfos = await fetchAllWaxData2({
        params: {
            code: CONTRACTS.APP_V2,
            scope: accountName,
            table: 'playersinfo',
        },
        customProcessor: ({row}) => ({
            boostBalance: row.boost_balance,
            pendingPrizes: row.pending_prizes,
            balances: row.balances,
        }),
    });

    return playerInfos[0];
};

module.exports = fetchPlayerInfo;
