const _ = require('lodash');
const CONTRACTS = require('../consts/CONTRACTS');
const fetchAllWaxData2 = require('../../core/fetchAllWaxData2');

const fetchCollectionSchemasWaxData = async ({
    collectionName,
}) => {
    const schemas = await fetchAllWaxData2({
        params: {
            code: CONTRACTS.ATOMIC_ASSETS,
            scope: collectionName,
            table: 'schemas',
        },
        customProcessor: ({row}) => ({
            schemaName: row.schema_name,
            templateSchema: row.format,
        }),
    });

    return schemas;
};

module.exports = fetchCollectionSchemasWaxData;
