const _ = require('lodash');
const announceWaxSale = require('./announceWaxSale')

const announceSales = ({
    assets,
    price,
}) => {
    return _(assets)
        .map(asset => announceWaxSale({
            accountName: asset.owner,
            assetIds: [asset.id],
            price,
        }))
        .flatten()
        .value();
};

module.exports = announceSales;
