const CONTRACTS = require('../consts/CONTRACTS');

const buyShopItem = ({
    accountName,
    itemId,
    price,
    tokenAccount,
}) => {
    return [
        {
            account: tokenAccount,
            name: 'transfer',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
              from: accountName,
              to: CONTRACTS.SHOP,
              quantity: price,
              memo: '',
            },
        },
        {
            account: CONTRACTS.SHOP,
            name: 'item.buy',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                player: accountName,
                id: itemId,
                price: price,
                token_account: tokenAccount,
            },
        },
    ];
};

module.exports = buyShopItem;
