const CONTRACTS = require('../../consts/CONTRACTS');

const mintFruits = ({
    accountName,
    mintingFruits,
}) => {
    return [
        {
            account: CONTRACTS.HARVESTING,
            name: 'mintfruits',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                user: accountName,
                fruit_template_ids: mintingFruits,
            },
        },
    ];
};

module.exports = mintFruits;
