const CONTRACTS = require('../consts/CONTRACTS');
const ORDER_CANCEL_ACTIONS = require('../consts/ORDER_CANCEL_ACTIONS');

const cancelMarketTokensSale = ({
    accountName,
    dexId,
    orderId,
    orderType,
}) => {
    return [
        {
            account: CONTRACTS.DEX,
            name: ORDER_CANCEL_ACTIONS[orderType],
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                executor: accountName,
                market_id: dexId,
                order_id: orderId,
            },
        }
    ];
};

module.exports = cancelMarketTokensSale
