const BG_CONTRACTS = require('../../consts/CONTRACTS');
const ATOMIC_CONTRACTS = require('../../../atomicassets/consts/CONTRACTS');

const feedBeasts = ({
    accountName,
    beastIds,
    fruitIds,
}) => {
    return [
        {
            account: ATOMIC_CONTRACTS.ATOMIC_ASSETS,
            name: 'transfer',
            authorization: [{
                actor: accountName,
                permission: 'active',
            }],
            data: {
                asset_ids: fruitIds,
                from: accountName,
                to: BG_CONTRACTS.FEEDING,
                memo: `beast;${beastIds.join(';')}`,
            },
        },
    ];
};

module.exports = feedBeasts;
