const CONTRACTS = require('../consts/CONTRACTS');

// example
// https://wax.bloks.io/transaction/a042ef078a794d1f95025c11e58fc2d9789e4294768f673e37b34fb2dd3d9104?tab=raw
const announceSale = ({
  accountName,
  saleId,
}) => {
    const actions = [
        {
            account: CONTRACTS.ATOMIC_MARKET,
            name: 'cancelsale',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
                sale_id: saleId
            },
        },
    ];

    return actions;
};

module.exports = announceSale;
