const _ = require('lodash');
const fetchCurrencyBalance = require('../../core/fetchCurrencyBalance');
const CONTRACTS = require('../consts/CONTRACTS');

const fetchAccountTokens = async ({accountName}) => {
    const tokens = await fetchCurrencyBalance({
        account: accountName,
        code: CONTRACTS.TOKEN,
        symbol: null,
    });

    return tokens;
}

module.exports = fetchAccountTokens;
