const CONTRACTS = require('../../consts/CONTRACTS');

const claimTokens = ({accountName}) => {
    return [
        {
            account: CONTRACTS.STAKING,
            name: 'claim',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                username: accountName,
            },
        },
    ];
};

module.exports = claimTokens;
