const _ = require('lodash');

const fetchWeathers = async () => {
  const res = await fetch('https://wax.greymass.com/v1/chain/get_table_rows', {
    headers: {
      accept: '*/*',
    },
    body: JSON.stringify({
      code: 'beastharvest',
      index_position: 1,
      json: true,
      key_type: '',
      limit: '100',
      lower_bound: null,
      reverse: false,
      scope: 'beastharvest',
      show_payer: false,
      table: 'weathers',
      upper_bound: null,
    }),
    method: 'POST',
  });

  const data = await res.json();

  if (data.rows.length === 0) {
    return [];
  }

  const weathers = _(data.rows)
    .map((weather) => ({
      id: weather.weather_id,
      claimPower: Number(weather.claim_power),
      name: weather.weather_name,
    }))
    .value();
  return weathers;
};

module.exports = fetchWeathers;
