const CONTRACTS = require('../consts/CONTRACTS');

const claimDeathRallyAssets = ({
    accountName,
    raceId,
}) => {
    return {
        account: CONTRACTS.APP_V2,
        name: 'claimasset',
        authorization: [
            {
                actor: accountName,
                permission: 'active',
            },
        ],
        data: {
            player: accountName,
            race_id: raceId,
            repair: false,
        },
    };
};

module.exports = claimDeathRallyAssets;
