const CONTRACTS = require('../consts/CONTRACTS');

const claimCharm = ({
    accountName,
}) => {
    return {
        account: CONTRACTS.APP_V2,
        name: 'charms.claim',
        authorization: [
            {
                actor: accountName,
                permission: 'active',
            },
        ],
        data: {
            player: accountName,
        },
    };
};

module.exports = claimCharm;
