// contract: beastreserve
// action: transfer
// from: "5oyiw.wam"
// to: "alcordexmain"
// quantity: "3000.0000 BGALPHA"
// memo: "53.15997000 WAX@eosio.token"


// from: "5oyiw.wam"
// to: "alcordexmain"
// quantity: "1.00000000 WAX"
// memo: "826446.2810 BGALPHA@beastreserve"

const CONTRACTS = require('../consts/CONTRACTS');

const announceMarketTokensSale = ({
    accountName,
    tokenContract,
    bidUnit,
    askUnit,
}) => {
    return [
        {
            account: tokenContract,
            name: 'transfer',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                from: accountName,
                to: CONTRACTS.DEX,
                quantity: bidUnit,
                memo: askUnit,
            },
        }
    ];
};

module.exports = announceMarketTokensSale;
