const _ = require('lodash');
const getTomorrowTimestamp = require('../../utils/getTomorrowTimestamp');
const CONTRACTS = require('../consts/CONTRACTS');
const fetchAllWaxData2 = require('../../core/fetchAllWaxData2');

const fetchTomorrowDeathRally = async () => {
    const deathRallyId = getTomorrowTimestamp();
    const deathRally = _(await fetchAllWaxData2({
        params: {
            code: CONTRACTS.APP_V2,
            scope: deathRallyId,
            table: 'deathrally',
        },
        customProcessor: ({row}) => ({
            id: row.id,
            accountName: row.player,
            vehicleId: row.vehicle_asset_id,
            driverIds: [
                row.driver1_asset_id,
                row.driver2_asset_id,
            ],
            time: row.time,
            pos: row.position,
            prizeIds: row.prize_ids,
        }),
    }))
        .sortBy(team => team.pos)
        .value();

    return deathRally;
};

module.exports = fetchTomorrowDeathRally;
