const _ = require('lodash');

const fetchPlantsPerWeathers = async () => {
  const res = await fetch('https://wax.greymass.com/v1/chain/get_table_rows', {
    headers: {
      accept: '*/*',
    },
    body: JSON.stringify({
      code: 'beastharvest',
      index_position: 1,
      json: true,
      key_type: '',
      limit: '100',
      lower_bound: null,
      reverse: false,
      scope: 'beastharvest',
      show_payer: false,
      table: 'pweathers',
      upper_bound: null,
    }),
    method: 'POST',
  });

  const data = await res.json();

  if (data.rows.length === 0) {
    return [];
  }

  const plantsPerWeathers = _(data.rows)
    .map((pw) => ({
      claimPower: Number(pw.claim_power),
      poolPower: Number(pw.pool_power),
      plantId: pw.plant_id,
      weatherId: pw.weather_id,
    }))
    .value();
  return plantsPerWeathers;
};

module.exports = fetchPlantsPerWeathers;
