const CONTRACTS = require('../consts/CONTRACTS');

const claimPrizes = ({
    accountName,
    limit,
}) => {
    return {
        account: CONTRACTS.APP_V2,
        name: 'claimprize',
        authorization: [
            {
                actor: accountName,
                permission: 'active',
            },
        ],
        data: {
            player: accountName,
            limit,
        },
    };
};

module.exports = claimPrizes;
