const CONTRACTS = require('../consts/CONTRACTS');

// example
// https://wax.bloks.io/transaction/5a9b0cd89be8dfa798093898afc9866cf70608335c8f9b8285664850c55be8df?tab=raw
const announceWaxSale = ({
  accountName,
  assetIds,
  price,
}) => {
    const actions = [
        {
            account: CONTRACTS.ATOMIC_MARKET,
            name: 'announcesale',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
                asset_ids: assetIds,
                listing_price: `${price.toFixed(8)} WAX`,
                maker_marketplace: 'market.nefty',
                seller: accountName,
                settlement_symbol: '8,WAX',
            },
        },
        {
            account: CONTRACTS.ATOMIC_ASSETS,
            name: 'createoffer',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
                memo: 'sale',
                recipient: CONTRACTS.ATOMIC_MARKET,
                recipient_asset_ids: [],
                sender: accountName,
                sender_asset_ids: assetIds,
            },
        },
    ];

    return actions;
};

module.exports = announceWaxSale;
