const CONTRACTS = require('../consts/CONTRACTS');
const GAME_VALUES = require('../consts/GAME_VALUES');

const joinDeathRally = ({
    accountName,
    vehicleId,
    driverIds,
}) => {
    return [
        {
            account: CONTRACTS.TOKEN,
            name: 'transfer',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
              from: accountName,
              to: CONTRACTS.APP_V2,
              quantity: `${GAME_VALUES.DEATH_RALLY_PRICE.value.toFixed(3)} ${GAME_VALUES.DEATH_RALLY_PRICE.symbol}`,
              memo: '',
            },
        },
        {
            account: 'atomicassets',
            name: 'transfer',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                from: accountName,
                to: CONTRACTS.APP_V2,
                asset_ids: [
                    vehicleId,
                    ...driverIds,
                ],
                memo: '',
            },
        },
    ];
};

module.exports = joinDeathRally;
