const CONTRACTS = require('../consts/CONTRACTS');

const claimFuel = ({accountName}) => {
    return {
        account: CONTRACTS.STAKING,
        name: 'claim',
        authorization: [
            {
                actor: accountName,
                permission: 'active',
            },
        ],
        data: {
            username: accountName,
        },
    };
};

module.exports = claimFuel;
