const fetchCurrencyBalance = require('../../core/fetchCurrencyBalance');
const {
    TOKEN: WAX_TOKEN,
} = require('../../core/consts/CONTRACTS');
const {
    TOKEN: ANCIENT_REALMS_TOKENS,
} = require('../../ancientrealms/consts/CONTRACTS');
const {
    TOKEN: ARKOVIA_TOKENS,
} = require('../../arkoviarealm/consts/CONTRACTS');
const {
    TOKENS: BEAST_GARDEN_TOKENS,
} = require('../../beastgarden/consts/CONTRACTS');
const {
    TOKEN: NOVARALLY_TOKENS,
    SCRAP_TOKEN: SCRAP_NOVARALLY_TOKENS,
} = require('../../novarallywax/consts/CONTRACTS');
const {
    TOKEN: NOVOPANGEA_TOKEN,
} = require('../../novopangea/consts/CONTRACTS');

const TOKEN_CONTRACTS = [
    WAX_TOKEN,
    ANCIENT_REALMS_TOKENS,
    ARKOVIA_TOKENS,
    BEAST_GARDEN_TOKENS,
    NOVARALLY_TOKENS,
    SCRAP_NOVARALLY_TOKENS,
    NOVOPANGEA_TOKEN,
];

const fetchAccountBalance = async ({accountName}) => {
    let tokens = [];

    for (let i = 0; i < TOKEN_CONTRACTS.length; i++) {
        const tokenContractName = TOKEN_CONTRACTS[i];
        const nextTokens = await fetchCurrencyBalance({
            account: accountName,
            code: tokenContractName,
            symbol: null,
        });

        tokens = [
            ...tokens,
            ...nextTokens,
        ];
    }

    return tokens;
};

module.exports = fetchAccountBalance;
