const fetchMarkets = require('./fetchMarkets');

const fetchTokenPoolPrice = async (token) => {
    const markets = await fetchMarkets();

    return markets[token.fullName]
      .pools.find(pool => pool.id === token.poolId)
      .symbols.find(symbol => symbol.symbol === token.name)
      .price;
}

module.exports = fetchTokenPoolPrice;
