const CONTRACTS = require('../consts/CONTRACTS');

const buyShare = ({
    accountName,
    manufacturerName,
    price,
    roundId,
    amount,
}) => {
    return [
        {
            account: CONTRACTS.TOKEN,
            name: 'transfer',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
              from: accountName,
              to: CONTRACTS.SHOP,
              quantity: price,
              memo: '',
            },
        },
        {
            account: CONTRACTS.SHOP,
            name: 'shares.buy',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                player: accountName,
                manufacturer_name: manufacturerName,
                round_id: roundId,
                amount,
            },
        },
    ];
};

module.exports = buyShare;
