const CONTRACTS = require('../consts/CONTRACTS');

const REPAIR_PRICE = 50000;

const repairVehicle = ({
    accountName,
    vehicleId,
}) => {
    return [
        {
            account: CONTRACTS.TOKEN,
            name: 'transfer',
            authorization: [
              {
                actor: accountName,
                permission: 'active',
              },
            ],
            data: {
              from: accountName,
              to: CONTRACTS.APP_V2,
              quantity: `${REPAIR_PRICE} SNAKOIL`,
              memo: '',
            },
        },
        {
            account: CONTRACTS.APP_V2,
            name: 'carrepair',
            authorization: [
                {
                    actor: accountName,
                    permission: 'active',
                },
            ],
            data: {
                player: accountName,
                asset_id: vehicleId,
            },
        },
    ];
};

module.exports = repairVehicle;
