    // MAX_LAND_RENT_PRICE_OBSD: 0.00000331,
    // // minimum building level to be placed to a land
    // MIN_RENT_BUILDING_LEVELS: {
    //     rest: 3,
    //     materials: 4,
    //     food: 4,
    //     energy: 4,
    // },
    // // minimum wage your workers agree to work
    // JOB_MIN_PROFITS_OBSD: [
    //     // level 1
    //     2,
    //     // level 2
    //     4.39400,
    //     // level 3
    //     6.59100,
