    //     // level 5
    //     8.72000,
    // ],
    // // maximum cost your workers agree to rest
    // REST_MAX_COST_OBSD: [
    //     // level 1
    //     1.1,
    //     // level 2
    //     1.510,
    //     // level 3
    //     1.873,
    //     // level 4
    //     2.055,
    //     // level 5
    //     2.237,
    // ],
    // // control values
    // ENABLE_WORKER_UPGRADES: true,
    // ENABLE_RENT_EXTERNAL_LANDS: true,
    // ENABLE_RENEW_RENT_LANDS: true,
    // ENABLE_REST_SKILLED_WORKERS: true,
    // ENABLE_REST_UNSKILLED_WORKERS: true,
    // ENABLE_SHIFT_SKILLED_WORKERS: true,
    // ENABLE_SHIFT_UNSKILLED_WORKERS: true,

function App() {
  const [botSettings, setBotSettings] = useState({});
  useEffect(() => {
    const lsBotSettings = localStorage.getItem('botSettings');
    if (lsBotSettings == null) {
      const settings = JSON.stringify([
        {
          name: 'claimWax',
          enabled: false,
        },
        {
          name: 'novopangea',
          enabled: false,
          params: ECONOMY_VALUES,
        },
      ]);
      localStorage.setItem('botSettings', settings);
      setBotSettings(settings);
    } else {
      setBotSettings(JSON.parse(lsBotSettings));
    }
  }, []);
  const login = useCallback(async () => {
    try {
      const userAccount = await wax.login();
      const pubKeys = wax.pubKeys;
      console.log(`AutoLogin enabled for account: ${userAccount}`);
      console.log(`Active: ${pubKeys[0]} Owner: ${pubKeys[1]}`);
    } catch (err) {
      console.log(err);
      console.log(err.message);
    }
  }, []);

  const [isBotStarted, setIsBotStarted] = useState(false);
  const _startBot = useCallback(async () => {
    startBot();
    setIsBotStarted(true);
  }, []);

  const _stopBot = useCallback(async () => {
    stopBot();
    setIsBotStarted(false);
  }, []);

  return (
    <div
      style={{
        padding: '12px 24px',
      }}
    >
      <div>Hi there, you visit a page with novopangea game. Ream more about this awesome game <a href="https://novopangea.io/" _target='blank'>here</a>.</div>
      <br />
      {isBotStarted ? (
        <button onClick={_stopBot}>Stop</button>
      ) : (
        <button onClick={_startBot}>Run</button>
      )}
      <button onClick={login}>Login</button>
      <br />
      <br />
    </div>
  );
}

export default App;
